-- Create views
DROP VIEW IF EXISTS dbo.vAccounts;
DROP VIEW IF EXISTS dbo.vDataDictionary;
DROP VIEW IF EXISTS dbo.vProducts;
DROP VIEW IF EXISTS dbo.vSalesPipeline;
DROP VIEW IF EXISTS dbo.vSalesTeams;
GO


CREATE VIEW dbo.vAccounts AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlcrm001.dfs.core.windows.net/transformed/2025-11-28/accounts/*.parquet',
    FORMAT = 'PARQUET'
) AS rows;
GO


CREATE VIEW dbo.vDataDictionary AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlcrm001.dfs.core.windows.net/transformed/2025-11-28/data_dictionary/*.parquet',
    FORMAT = 'PARQUET'
) AS rows;
GO


CREATE VIEW dbo.vProducts AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlcrm001.dfs.core.windows.net/transformed/2025-11-28/products/*.parquet',
    FORMAT = 'PARQUET'
) AS rows;
GO


CREATE VIEW dbo.vSalesPipeline AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlcrm001.dfs.core.windows.net/transformed/2025-11-28/sales_pipeline/*.parquet',
    FORMAT = 'PARQUET'
) AS rows;
GO


CREATE VIEW dbo.vSalesTeams AS
SELECT *
FROM OPENROWSET(
    BULK 'https://dlcrm001.dfs.core.windows.net/transformed/2025-11-28/sales_teams/*.parquet',
    FORMAT = 'PARQUET'
) AS rows;
GO
